###PUESTO COMERCIAL
#BASIC

#ORIGEN
ORIGEN_1_PUESTO_COMERCIAL = {text:"Accidental. El puesto comercial se creó por accidente, por ejemplo, después de que se averiase o perdiese una caravana. Lasmedidas que se tomaron para remediar el accidente acabaron dando lugar al puesto comercial"}
ORIGEN_2_PUESTO_COMERCIAL = {text:"Empresa comercial. Un emprendedor adinerado decidió fundar un puesto comercial."}
ORIGEN_3_PUESTO_COMERCIAL = {text:"Cruce de caminos. El puesto comercial está en la intersección de varias de las principales rutas de comercio."}
ORIGEN_4_PUESTO_COMERCIAL = {text:"Puesto militar. El puesto comercial se construyó en las ruinas de una antigua fortaleza o torre de guardia. Estas estructuras llevaban mucho tiempo abandonadas o los habitantes de la zona decidieron reutilizarlas."}
ORIGEN_5_PUESTO_COMERCIAL = {text:"Tierra de nadie. El puesto comercial se ha establecido en una zona neutral, donde los ejércitos pueden comprar mercancías sin adentrarse en terreno enemigo."}
ORIGEN_6_PUESTO_COMERCIAL = {text:"Nativo. Alguien de la zona fundó el puesto comercial tras considerar que el comercio con la gente que pasaba por la zona tenía potencial."}
ORIGEN_7_PUESTO_COMERCIAL = {text:"Parada nocturna. Originalmente el puesto comercial era una casa de gran tamaño que ofrecía estancias de una noche a viajeros cansados. El lugar pronto evolucionó como respuesta a la creciente demanda de alojamiento."}
ORIGEN_8_PUESTO_COMERCIAL = {text:"Experto en naturaleza. El puesto comercial fue fundado por un trampero, un cazador o un guía, que estableció un campamento para ayudar a quienes viajasen por la zona."}

#ESPACIALIDAD
SPECIALITY_1_PUESTO_COMERCIAL = {text:"Métodos de envío atípicos. A este puesto comercial se lo conoce por su forma única y efectiva de transportar mercancías."}
SPECIALITY_2_PUESTO_COMERCIAL = {text:"Comida y bebida", suboptions:{1:"Una comida excepcional",2:"Una comida excepcional",3:"Una comida excepcional",4:"Gran surtido de bebidas variadas y de buena calidad",5:"Gran surtido de bebidas variadas y de buena calidad",6:"Gran surtido de bebidas variadas y de buena calidad",}}
SPECIALITY_3_PUESTO_COMERCIAL = {text:"Hospitalidad. La posada principal es de gran calidad y ofrece un servicio excelente, habitaciones cómodas y buena comida. Ninguna posada del lugar tendrá calidad pobre"}
SPECIALITY_4_PUESTO_COMERCIAL = {text:"Información. Este puesto comercial es famoso por ser una buena fuente de información. Aunque las personas aquí no lo saben todo, existen muchas posibilidades de encontrarse con rumores verídicos, sabiduría tradicional, noticias o algún detalle interesante."}
SPECIALITY_5_PUESTO_COMERCIAL = {text:"Contactos. El puesto comercial es famoso por contar con gente que sabe encontrar cosas. Si no tienen (o no saben) lo que estás buscando, te pueden recomendar a alguien."}
SPECIALITY_6_PUESTO_COMERCIAL = {text:"Contratistas sin escrúpulos. Este puesto comercial es famoso por tener gente que puede hacer cualquier cosa si hay suficiente dinero de por medio. Mercenarios tipo: ", suboptions:{1: "Brutos y matones", 2: "Agentes clandestinos", 3: "Arqueros y honderos" ,4: "Escribas y administrativos", 5: "Guías y rastreadores", 6: "Caravaneros y monturas", 7:"Académicos arcanos", 8: "Mercenarios mágicos", 9:"Orientación sacerdotal", 10:"Manos de la divinidad"}}





